mapper.select:
select fk_idx as idx_biz, name, latitude, longitude, prontimg, startpoint as avg_startpoint from ( select fk_idx, name, latitude, longitude, prontimg, cnt from ( select fk_idx, count(*) as cnt from have_tag H JOIN biz_info B on H.fk_idx = B.idx_biz where fk_tag_uid in (select fk_tag_uid from have_tag where fk_idx = #{LOGINUSER_IDX}) group by fk_idx order by cnt desc ) V JOIN ( select idx, name, latitude, longitude, prontimg from member M JOIN biz_info B on M.idx = B.idx_biz ) T on V.fk_idx = T.idx order by cnt desc ) Y JOIN ( select idx_biz, nvl(round(avg(startpoint)), 0) as startpoint from biz_info B JOIN member M on B.idx_biz = M.idx FULL JOIN review R on M.idx = R.fk_idx_biz group by idx_biz ) X on Y.fk_idx = X.idx_biz
order by cnt desc, startpoint desc
order by ${NUMBERS}
